    $(document).ready(function () {
        function delay(callback, ms) {
            var timer = 0;
            return function () {
                var context = this, args = arguments;
                clearTimeout(timer);
                timer = setTimeout(function () {
                    callback.apply(context, args);
                }, ms || 0);
            };
        }

        var data_json = [];
        $("input#check_api").keydown(function () {
            var check = $(this).val();
