                var str1 = element.value;
                if (str1.indexOf(check) != -1 || str1.indexOf(check.toUpperCase()) != -1) {
                    arr.push(element);
                }
            })

            $(this).autocomplete({
                source: arr,
                select: function (event, ui) {
                    window.open(ui.item.url, '_self');
                    ;
                }
            });
        });


    });
